end;

var
  S: String;
begin
  case ProcessorArchitecture of
    paX86: S := 'x86';
    paX64: S := 'x64';
    paArm64: S := 'Arm64';
  else
    S := 'Unrecognized';
  end;
  MsgBox('Windows architecture: ' + S, mbInformation, MB_OK);
end;

